theme_mods.custom_css_string:
/* header-logo-width 
=========================*/
.aux-logo-header .aux-logo-anchor{ max-width:80px; }
/* header-logo-max-height-type 
=========================*/
.aux-logo-header .aux-logo-anchor > img { max-height:85px; }
/* site-frame-background-color 
=========================*/
@media screen and (min-width: 700px) { .aux-framed .aux-side-frames, body.aux-framed:after,
            .aux-framed .aux-side-frames:before, .aux-framed .aux-side-frames:after{ background-color:#111111; } }
/* site-header-container-height 
=========================*/
.site-header-section .aux-header-elements:not(.aux-vertical-menu-elements), .site-header-section .aux-fill .aux-menu-depth-0 > .aux-item-content { height:85px; }
/* site-transparent-header-bgcolor 
=========================*/
.site-header-section { background-color:#FFFFFF; }
/* site-header-container-scaled-height 
=========================*/
.aux-top-sticky .site-header-section.aux-sticky .aux-fill .aux-menu-depth-0 > .aux-item-content, .aux-top-sticky .site-header-section.aux-sticky .aux-header-elements { height:80px; }
/* sticky-header-color 
=========================*/
.site-header-section.aux-sticky { background-color:#FFFFFF; }
/* site-vertical-menu-background-color 
=========================*/
.aux-vertical-menu-side  { background-color:#FFF; }
/* site-header-navigation-item-height 
=========================*/
.site-header-section .aux-middle .aux-menu-depth-0 > .aux-item-content { height:60px; }
/* site-mobile-header-toggle-button-color 
=========================*/
.site-header-section .aux-header .aux-burger:before, .site-header-section .aux-header .aux-burger:after, .site-header-section .aux-header .aux-burger .mid-line{ border-color:#3d3d3d; }
/* site-menu-full-screen-background-color 
=========================*/
#fs-menu-search:before { background-color:rgba(255, 255, 255, 0.95); }
/* site-top-header-background-color 
=========================*/
#top-header{background-color:#FFFFFF;}
/* post-title-bar-overlay-pattern-opacity 
=========================*/
.single-post .aux-overlay-bg-hash::before { opacity:0.15; }
/* page-title-bar-overlay-pattern-opacity 
=========================*/
.page .aux-overlay-bg-hash::before { opacity:0.5; }
/* subfooter-bar-layout-bg-color 
=========================*/
.aux-subfooter-bar { background-color:#fafafa; }
/* subfooter-bar-top-border-color 
=========================*/
.aux-subfooter-bar { border-top:1px solid #EAEAEA; }
/* subfooter-layout-bg-image-position 
=========================*/
.aux-subfooter{background-position:center center;}
/* subfooter-layout-bg-image-size 
=========================*/
.aux-subfooter{background-size:cover;}
/* subfooter-layout-bg-image-repeat 
=========================*/
.aux-subfooter{background-repeat:no-repeat;}
/* subfooter-top-border-color 
=========================*/
.aux-subfooter { border-top:1px solid #EAEAEA; }
/* site-footer-bg-color 
=========================*/
.aux-site-footer{background-color:#1A1A1A;}
/* footer-top-border-color 
=========================*/
.aux-site-footer{border-top:1px solid #EAEAEA;}
/* footer-top-border-width 
=========================*/
.aux-site-footer { border-top-width:1px; }
/* site-secondary-logo-max-height 
=========================*/
.aux-logo-footer .aux-logo-anchor img { max-height:50px; }
/* product-title-bar-overlay-pattern-opacity 
=========================*/
.single-product .aux-overlay-bg-hash::before { opacity:0.5; }
/* single-post-title-typography 
=========================*/
.single-post .aux-primary .hentry .entry-title{ font-family:'Poppins'; color:rgb(0, 0, 0); font-size:30px; font-weight:bold; line-height:43px; letter-spacing:-0.6px; } 
/* single-post-content-typography 
=========================*/
.single-post .hentry .entry-content{ font-family:'Montserrat'; color:rgba(61, 61, 61, 0.9); font-size:16px; font-weight:500; line-height:30px; } 
/* single-post-info-typography 
=========================*/
.single-post .hentry .entry-info{ font-family:'Montserrat'; color:rgb(0, 0, 0); font-size:16px; font-weight:400; text-transform:capitalize; line-height:19px; } 
/* single-post-info-terms-typography 
=========================*/
.single-post .hentry .entry-info a{ color:rgb(0, 0, 0); } 
/* single-post-meta-typography 
=========================*/
.single-post .hentry footer.entry-meta .entry-tax{ font-family:'Montserrat'; color:rgb(0, 0, 0); font-size:16px; font-weight:bold; text-transform:capitalize; font-style:normal; line-height:19px; } 
/* single-post-meta-terms-typography 
=========================*/
.single-post .hentry footer.entry-meta .entry-tax a, .single-post .hentry footer.entry-meta .entry-tax i{ font-family:'Montserrat'; color:rgb(207, 207, 207); font-size:16px; font-weight:400; text-transform:capitalize; text-decoration:none; line-height:19px; } 
/* comment-forms-replay-title-typo 
=========================*/
#reply-title{ font-family:'Poppins'; color:rgb(0, 0, 0); font-size:20px; font-weight:bold; line-height:28px; } 
/* comment-forms-notes-typo 
=========================*/
#commentform .comment-notes{ font-family:'Montserrat'; color:rgba(35, 32, 63, 0.6); font-size:16px; font-weight:400; line-height:19px; } 
/* comment-forms-button-typo 
=========================*/
#commentform .form-submit input[type="submit"]{ font-family:'Montserrat'; color:rgb(255, 255, 255); font-size:16px; font-weight:400; text-transform:none; text-decoration:underline; line-height:19px; } 
/* comment-author-typo 
=========================*/
.aux-commentlist .comment-author .fn, .aux-commentlist .comment-author .fn a{ font-family:'Poppins'; color:rgb(0, 0, 0); font-size:20px; font-weight:bold; line-height:28px; } 
/* comment-info-typo 
=========================*/
.aux-commentlist .comment .comment-author time a{ font-family:'Montserrat'; color:rgba(0, 0, 0, 0.7); font-size:15px; font-weight:400; line-height:19px; } 
/* comment-content-typo 
=========================*/
.aux-commentlist .comment .comment-body{ font-family:'Montserrat'; color:rgba(0, 0, 0, 0.6); font-size:16px; line-height:19px; } 
/* comment-forms-response-title-typo 
=========================*/
.comments-title, .comment-reply-title{ font-family:'Poppins'; color:rgb(0, 0, 0); font-size:20px; font-weight:bold; line-height:28px; } 
/* product-setting-section-shop-page-title-typo 
=========================*/
.woocommerce-page .page-title{ font-family:'Poppins'; color:rgb(0, 0, 0); font-size:30px; font-weight:bold; line-height:46px; } 
/* product-setting-section-shop-page-bread-typo 
=========================*/
.woocommerce-page .aux-breadcrumbs > span, .woocommerce-page .aux-breadcrumbs > span a{ font-family:'Montserrat'; color:rgb(207, 207, 207); font-size:16px; font-weight:500; line-height:19px; } 
/* product-setting-section-shop-page-result-typo 
=========================*/
.woocommerce-page .woocommerce-result-count{ font-family:'Poppins'; color:rgb(0, 0, 0); font-size:20px; font-weight:bold; text-transform:capitalize; line-height:30px; } 
/* product-setting-section-shop-page-product-title-typo 
=========================*/
.aux-shop-archive .products-loop .auxshp-loop-title{ font-family:'Poppins'; color:rgb(0, 0, 0); font-size:16px; font-weight:bold; line-height:23px; } 
/* product-setting-section-shop-page-product-meta-fields 
=========================*/
.aux-shop-archive .products-loop .aux-shop-meta-fields span{ font-family:'Montserrat'; color:rgba(0, 0, 0, 0.6); font-size:16px; font-weight:400; line-height:19px; } 
/* product-setting-section-shop-page-product-price-typo 
=========================*/
.aux-shop-archive .products-loop .price .amount, .aux-shop-archive .products-loop .price ins .amount{ font-family:'Montserrat'; color:rgb(0, 0, 0); font-size:25px; font-weight:bold; line-height:30px; } 
/* product-setting-section-shop-page-product-btn-typo 
=========================*/
.aux-shop-archive .products-loop .loop-tools-wrapper .button{ font-family:'Montserrat'; color:rgb(0, 0, 0); font-size:16px; font-weight:500; text-decoration:underline; line-height:19px; } 
/* sidebar-common-widget-title-typography 
=========================*/
.aux-sidebar .widget-title{ font-family:'Poppins'; color:rgb(0, 0, 0); font-size:20px; font-weight:bold; text-transform:capitalize; line-height:28px; letter-spacing:-0.8px; } 
/* product-setting-section-shop-page-pagination 
=========================*/
.aux-shop-archive .aux-pagination{ font-family:'Montserrat'; color:rgb(0, 0, 0); font-size:17px; font-weight:600; } 
/* sidebar-global-typography 
=========================*/
.aux-sidebar{ font-family:'Montserrat'; font-size:16px; } 
/* product-setting-section-single-appearance-meta-field 
=========================*/
.single-product .aux-shop-meta-field span{ font-family:'Montserrat'; color:rgba(0, 0, 0, 0.7); font-size:15px; font-weight:400; line-height:19px; } 
/* product-setting-section-single-appearance-title 
=========================*/
.single-product .entry-title{ font-family:'Poppins'; color:rgb(0, 0, 0); font-size:35px; font-weight:bold; line-height:49px; letter-spacing:-0.6px; } 
/* product-setting-section-single-appearance-star-text 
=========================*/
.single-product .woocommerce-review-link{ font-family:'Montserrat'; color:rgba(0, 0, 0, 0.7); font-size:15px; font-weight:400; line-height:19px; } 
/* product-setting-section-single-appearance-description 
=========================*/
.single-product .woocommerce-product-details__short-description{ font-family:'Montserrat'; color:rgba(0, 0, 0, 0.7); font-size:15px; font-weight:400; line-height:25px; } 
/* product-setting-section-single-appearance-price 
=========================*/
.single-product .price .amount, .single-product .price ins .amount{ font-family:'Montserrat'; color:rgb(0, 0, 0); font-size:25px; font-weight:bold; line-height:35px; } 
/* product-setting-section-single-appearance-add-to-btn 
=========================*/
.single-product .cart .auxshp-add-to-cart{ font-family:'Montserrat'; color:rgb(0, 0, 0); font-size:15px; font-weight:bold; text-transform:capitalize; line-height:19px; } 
/* product-setting-section-single-appearance-category 
=========================*/
.single-product .auxshp-cats-wrapper .auxshp-label{ font-family:'Poppins'; color:rgb(0, 0, 0); font-size:16px; font-weight:bold; line-height:23px; } 
/* product-setting-section-single-appearance-category-terms 
=========================*/
.single-product .auxshp-cats-wrapper .auxshp-meta-terms, .single-product .auxshp-cats-wrapper .auxshp-meta-terms a{ font-family:'Montserrat'; color:rgb(207, 207, 207); font-size:16px; font-weight:500; line-height:19px; } 
/* product-setting-section-single-appearance-tabs-title 
=========================*/
.woocommerce-page .woocommerce-tabs .tabs li a{ font-family:'Poppins'; color:rgb(207, 207, 207); font-size:16px; font-weight:500; line-height:23px; letter-spacing:-0.6px; } 
/* product-setting-section-single-appearance-tabs-content 
=========================*/
.single-product .woocommerce-tabs .tabs-content{ font-family:'Montserrat'; color:rgb(0, 0, 0); font-size:16px; font-weight:500; line-height:23px; letter-spacing:-0.6px; } 
/* product-setting-section-single-appearance-review-title 
=========================*/
.single-product .woocommerce-Reviews-title{ color:rgb(0, 0, 0); } 
/* product-setting-section-single-appearance-review-author 
=========================*/
.single-product .woocommerce-review__author{ font-family:'Poppins'; color:rgb(0, 0, 0); font-size:20px; font-weight:bold; line-height:28px; } 
/* product-setting-section-single-appearance-review-date 
=========================*/
.single-product .woocommerce-review__published-date{ font-family:'Montserrat'; color:rgba(0, 0, 0, 0.7); font-size:15px; font-weight:400; line-height:19px; } 
/* product-setting-section-single-appearance-review-content 
=========================*/
.single-product .woocommerce-Reviews .comment-text{ font-family:'Montserrat'; color:rgba(0, 0, 0, 0.6); font-size:16px; font-weight:400; line-height:23px; } 
/* product-related-posts-label-typo 
=========================*/
.single-product .auxshp-related-products .widget-title{ font-family:'Poppins'; color:rgb(0, 0, 0); font-size:30px; font-weight:bold; line-height:43px; } 
/* product-related-posts-title-typo 
=========================*/
.aux-widget-related-products .auxshp-entry-main .auxshp-loop-title{ font-family:'Poppins'; color:rgb(0, 0, 0); font-size:16px; font-weight:bold; line-height:23px; } 
/* product-related-posts-price-typo 
=========================*/
.aux-widget-related-products .auxshp-entry-main .price .amount , .aux-widget-related-products .auxshp-entry-main .price ins .amount{ font-family:'Montserrat'; color:rgb(0, 0, 0); font-size:25px; font-weight:bold; line-height:30px; } 
/* product-related-posts-btn-typo 
=========================*/
.aux-widget-related-products .loop-tools-wrapper .button{ font-family:'Montserrat'; color:rgb(0, 0, 0); font-size:16px; font-weight:500; text-decoration:underline; line-height:19px; }

