selector .type-product {
    display: flex;
    flex-flow: row wrap;
    align-items: center;
    justify-content: flex-end;
    position: relative;
    padding-bottom: 0;
    padding-right: 20px !important;
    padding-left: 20px !important;
    margin-top: 180px;
}
selector .woocommerce-LoopProduct-link {
    display: block;
    max-width: 218px;
    position: absolute;
    left: 20px;
    top: -60px;
    box-shadow: 20px 15px 60px rgba(0, 0, 0, 0.16);
    z-index: 2;

}

selector .auxshp-entry-main {
    position: relative;
    max-width: 300px;
    padding: 46px 30px 53px 36px;
    z-index: 2;
}

selector .type-product:before {
    content: '';
    background: linear-gradient(301deg, rgba(239,239,239,1) 0%, rgba(242,241,251,1) 100%);
    position: absolute;
    top: 0;
    left: 60px;
    right: 15px;
    bottom: 0;
    border-radius: 20px;
}

selector .type-product:after {
    content: '';
    background: linear-gradient(121deg, #876FE5 0%, #251A94 100%);
    position: absolute;
    top: 0;
    left: 60px;
    right: 15px;
    bottom: 0;
    border-radius: 20px;
    opacity: 0;
    transition: 0.3s opacity;
    z-index: 1;
}

selector .type-product:hover:after {
    opacity: 1;
}


selector .type-product:hover .auxshp-label,
selector .type-product:hover .woocommerce-Price-amount{
    color: #FFF !important;
}

selector .type-product:hover .aux-shop-meta-field span,
selector .type-product:hover .aux-shop-desc-wrapper{
    color: rgba(255,255,255,0.7) !important;
}

