post_meta.aux_page_custom_css:
.aux-shop2-home-custom-transform {
    position: absolute;
    right: 0;
    top: calc(50% + 120px);
    transform: translateY(-50%);
}
.aux-shop2-home-countdown .aux-countdown-item:first-child {
    padding-left: 0 !important;
}
.aux-shop2-home-bg-pos {
    background-position: right;
}

.hentry .entry-info [class^="entry-"]+[class^="entry-"]:before, .aux-widget-post-slider .aux-latest-posts-slider .aux-slide-info .entry-tax a::after, .hentry .entry-info .entry-tax a::after {
    background-color: rgba(112, 112, 112, 0.2);
    width: 24px;
    height: 1px;
    border-radius: 0;
}
.hentry .entry-main,
.hentry .entry-content {
    margin-bottom: 0;
}

.aux-resp .widget-title {
    text-align: left;
}

@media screen and (min-width: 1025px ) and (max-width: 1200px)  {
    .aux-shop2-home-custom-transform {
        bottom: 0;
        right: auto;
        top: auto;
        transform: none;
        width: 100% !important;
        text-align: center !important;
    }

    
    .aux-shop2-section-img-resp-fix > .elementor-container  {
        min-height: 1400px !important;
    }
}
@media screen and (max-width: 1200px) {
    .aux-menu-image-item {
        display :none;
    }
    .aux-shop2-home-custom-transform  .aux-modern-heading-divider {
        margin: auto;
    }
    
    .aux-shop2-home-custom-transform .elementor-widget-aux_button {
        text-align: center !important;
    }
}

@media screen and (max-width: 767px) {
    .aux-cart-wrapper {
        padding: 0;
    }
    
    .aux-shop2-section-img-resp-fix > .elementor-container{ 
        align-items: flex-end !important;
    }
    
    .aux-shop2-home-custom-transform {
        right: auto;
        top: auto;
        transform: none;
        position: relative;
    }
}
@media screen and (max-width: 1024px) { 
    .aux-shop2-home-bg-pos  {
        background-size: cover !important;
    }
}

@media screen and (max-width: 480px) { 
    .aux-shop2-section-img-resp-fix > .elementor-container{
        min-height: 790px !important;
    }
}

post_meta.aux_page_custom_js:
// Auxin Wrapper Cart Custom JS 

jQuery(function ($) {
    var wrapper = $('.aux-shopping-basket').not('#shopping-basket-burger'),
        totalItems = wrapper.find('.aux-cart-contents  span').text(),
        text = $('<span />').addClass('aux-sub-total-desc').html(totalItems + ' Item In Shopping Bag'),
        finalAmount = wrapper.find('.aux-card-final-amount');
    
    if ( totalItems > 0 ) {
        $('.aux-cart-wrapper.aux-elegant-cart .aux-card-final-amount').append(text);

    }

});
