    margin: 0 0 30px;
    border-bottom: 1px solid #E9E9E9;
}

selector .entry-content, selector .auxnew-header-col{
    margin: 0;
}

selector .aux-news-element-header{
    display: flex;
    align-items: center;
    position: relative;
}

selector .aux-filters ul > li{
    float: right;
}

selector .aux-news-element-header::after{
    content: "";
    position: absolute;
    left: 15px;
    right: 15px;
    bottom: 0px;
    height: 2px;
    background: #000000;
    display: block;
    transform: translateY(5px);
}

selector .aux-news-element-header::before{
    content: "";
    position: absolute;
    left: 15px;
    right: 15px;
    top: 0px;
    height: 1px;
    background: rgba(0, 0, 0, 0.15);
    display: block;
    transform: translateY(-5px);
}

selector .entry-date::before{
    display: none !important;
}

selector .auxnew-main-post{
    position: relative;
}

selector .auxnew-main-post .entry-main{
    position: relative;
}

selector .auxnew-main-post .entry-header{
    width: 50%;
    background: #fff;
    position: absolute;
    top: 0;
    transform: translateY(-100%);
    padding: 29px 39px 29px 0;

}

selector .auxnew-main-post::before{
    content: "";
    position: absolute;
    right: 0;
    top: 30px;
    width: 1px;
    bottom: 0;
    background: #E9E9E9;
}

selector .auxnew-side-posts .hentry:last-child{
    margin: 0;
    border-bottom: 0;
}

selector .aux-small-posts{
    display: flex;
    align-items: center;
}

selector .auxin-news-header-sep{
    display: none;
}

@media (max-width: 1023px)  {
    selector .auxnew-side-posts{
        padding-left: 15px !important;
    }    
    selector .auxnew-main-post::before{
        display: none;
    }
    selector .auxnew-main-post::after{
        content: "";
        position: absolute;
        left: 15px;
        right: 15px;
        bottom: -25px;
        height: 1px;
        background: #E9E9E9;
    }    
}

@media (max-width: 767px)  {
    selector .auxnew-side-posts .aux-thumbnail-col{
        display: none;
    }    
    selector .auxnew-side-posts .aux-entry-col{
        width: 100%;
    }    

    selector .auxnew-main-post .entry-header{
        width: 100%;
        background: transparent;
        position: relative;
        transform: translateY(0);
        padding: 0;

    }    
