selector .avatar{
    width: 41px !important;
    height: 41px !important;
}

selector .aux-news-element-header{
    display: flex;
    align-items: center;
    position: relative;
}

selector .aux-filters ul > li{
    float: right;
}

selector .aux-news-element-header::after{
    content: "";
    position: absolute;
    left: 15px;
    right: 15px;
    bottom: 0px;
    height: 2px;
    background: #000000;
    display: block;
    transform: translateY(5px);
}

selector .entry-date::before{
    display: none !important;
}

selector .auxnew-main-post .entry-header{
    background: #fff;
    padding: 9px 39px 29px 0;
    margin-bottom: 15% !important;

}



selector .auxnew-main-post::before{
    content: "";
    position: absolute;
    right: 5px;
    top: 0;
    width: 1px;
    bottom: 0;
    background: #E9E9E9;
}

selector .auxnew-big-post{
    display: flex;
    flex-direction: row-reverse;
    margin-top: 0 !important;
}

selector .auxnew-big-post .entry-media {
    width: 70%;
    margin: 0 0 0 -15%;
}

selector .auxnew-big-post .entry-main {
    width: 45%;
}

selector .auxnew-big-post .entry-info, selector .auxnew-big-post .entry-content, selector .auxnew-big-post .entry-meta {
    padding-right: 45%;
    margin-bottom: 10% !important;
}

selector .auxnew-side-posts{
    display: none;
}


@media (max-width: 1023px)  {
    selector .auxnew-big-post{
        display: block;
    }
    selector .auxnew-main-post .entry-header{
        background: #fff;
        padding: 0;
        margin-bottom: 5% !important;
    }
    selector .aux-news-side .auxnew-main-post {
        padding-right: 0;
    }        
    selector .auxnew-main-post::before{
        content: "";
        position: absolute;
        right: 15px;
        left: 0;
        top: inherit;
        bottom: -20px;
        width: 100%;
        height: 1px;
    }       
    selector .auxnew-big-post .entry-media {
        width: 100%;
        margin: 0;
    }
    selector .auxnew-big-post .entry-main {
        width: 100%;
    }  
    selector .auxnew-big-post .entry-info, selector .auxnew-big-post .entry-content, selector .auxnew-big-post .entry-meta {
        padding-right: 0;
        margin-bottom: 5% !important;
    }    
}

@media (max-width: 767px)  {
    selector .auxnew-side-posts{
        display: block;
    }    
    selector .auxnew-side-posts .hentry{
        margin: 0;
    }       
}
